// ######################################################
// CORE: INIT
// ######################################################

import { PLog, PCallback } from './modules/nodee';
import { GlobalConfig } from '../../../config';

// clear the console
if (GlobalConfig.PLog_CLEAR_CONSOLE_ON_STARTUP) {
  PLog.clear();
}

PLog.put(GlobalConfig.APP_NAME.toUpperCase(), true);

try {
  require('./drivers').default();
} catch (e) {
  PLog.putException('[db] could not connect to database, please check your connection', e);
}

try {
  require('./express');
} catch (e) {
  PLog.putException('[server] cannot run express server', e);
}

try {
  require(__DIR_APP + 'setup/post-init');
} catch (e) {
  PLog.putException('[express] no post-init found or it has some issues', e);
}